using System;
using System.Collections.Generic;
using System.Linq;
using System.Text;
using System.Threading.Tasks;

namespace SecurityLibrary
{
    public class Monoalphabetic : ICryptographicTechnique<string, string>
    {
        public string Analyse(string plainText, string cipherText)
        {
            //throw new NotImplementedException();
            plainText = plainText.ToLower();
            cipherText = cipherText.ToLower();

            Dictionary<char, char> mapDic = new Dictionary<char, char>();
            for (int i = 0; i < cipherText.Length; i++)
            {
                if (mapDic.ContainsKey(plainText[i]))
                {
                    continue;
                }
                else
                {
                    mapDic[plainText[i]] = cipherText[i];
                }

            }

            string alphabet = "ABCDEFGHIJKLMNOPQRSTUVWXYZ".ToLower();
            for (int i = 0; i < alphabet.Length; i++)
            {
                if (mapDic.ContainsKey(alphabet[i]) == false)
                {
                    for (int j = 0; j < alphabet.Length; j++)
                    {
                        if (mapDic.ContainsValue(alphabet[j]) == false)
                        {
                            mapDic[alphabet[i]] = alphabet[j];
                        }
                    }
                }
            }

            Dictionary<char, char> orderedDic = mapDic.OrderBy(n => n.Key).ToDictionary(n => n.Key, n => n.Value);
            string key = "";

            foreach (var c in orderedDic)
            {
                key += c.Value;
            }


            return key;

        }

        public string Decrypt(string cipherText, string key)
        {
            //throw new NotImplementedException();
            cipherText = cipherText.ToLower();
            key = key.ToLower();

            string alphabet = "ABCDEFGHIJKLMNOPQRSTUVWXYZ";
            string plain = "";

            for (int i = 0; i < cipherText.Length; i++)
            {
                for (int j = 0; j < key.Length; j++)
                {
                    if (cipherText[i] == key[j])
                    {
                        plain = plain + alphabet[j];
                    }
                }
            }
            return plain.ToLower();

        }

        public string Encrypt(string plainText, string key)
        {
            //throw new NotImplementedException();
            plainText = plainText.ToUpper();
            key = key.ToUpper();

            string alphabet = "ABCDEFGHIJKLMNOPQRSTUVWXYZ";
            string cipher = "";
            for (int i = 0; i < plainText.Length; i++)
            {
                for (int j = 0; j < key.Length; j++)
                {
                    if (plainText[i] == alphabet[j])
                    {
                        cipher = cipher + key[j];
                    }
                }
            }
            return cipher;
        }







        /// <summary>
        /// Frequency Information:
        /// E   12.51%
        /// T	9.25
