        /// Q	0.11
        /// Z	0.09
        /// </summary>
        /// <param name="cipher"></param>
        /// <returns>Plain text</returns>
        /// 

        public string AnalyseUsingCharFrequency(string cipher)
        {
            string largePlain = "".ToLower();

            Dictionary<char, int> orderedbyfrequancyC = GetFrequency(ref cipher);
            Dictionary<char, int> orderedbyfrequancyP = GetFrequency(ref largePlain);

            char[] freqText = orderedbyfrequancyC.Keys.ToArray();
            char[] freqPlain = orderedbyfrequancyP.Keys.ToArray();
            string plainText = "";

            for (int i = 0; i < cipher.Length; i++)
            {
                for (int j = 0; j < freqText.Length; j++)
                {
                    if (cipher[i] == freqText[j])
                    {
                        plainText = plainText + freqPlain[j];
                    }
                }
            }
            return plainText;

        }
        static Dictionary<char, int> GetFrequency(ref string input)
        {
            Dictionary<char, int> freq = new Dictionary<char, int>();

            for (int i = 0; i < input.Length; i++)
            {
                if (freq.ContainsKey(input[i]))
                {
                    freq[input[i]] = freq[input[i]] + 1;
                }
                else
                {
                    freq[input[i]] = 1;
                }
            }
            return freq.OrderByDescending(n => n.Value).ToDictionary(n => n.Key, n => n.Value);

        }

    }
